import Header from './Components/Header'
import './App.css';
import Footer from './Components/Footer';
import Content from './Components/Content';
import Main from './Components/Main';
import VoteButton from './Components/VoteButton';
import {useCookies} from 'react-cookie'
import Cookies from 'universal-cookie';
import {useState} from 'react'

function App() {

    const cookies = new Cookies();
    const [jokesContentId,setJokesContentId] = useState(()=>{
      if(cookies.get('lastVote')){
        return cookies.get('lastVote')
      }
      return 0;
    })


  const HandleVote=(JSC,isFunny)=>{
    isFunny?cookies.set(JSC,true):cookies.set(JSC,false)
    cookies.set('lastVote',JSC)

  }


  return (

    <div className="App">
      <Header/>

      <VoteButton onClick={HandleVote(jokesContentId,true)} title='This is funny!'/>
      <VoteButton title='This is not funny!'/>
      <Footer/>
    </div>
  );
}

